sourcesContent:
const redisOptions = require('./redisConnectionOptions');
const thumbnail = require('image-thumbnail');
const Bull = require('bull');
const fileQueue = new Bull('image-processing', redisOptions);
const dbClient = require('./utils/db');
const fs = require('fs');
const path = require('path');

fileQueue.process(async (job) => {
    console.log('Processing job:', job.data);
    const { fileId, userId } = job.data;

    if (!fileId || !userId) {
        throw new Error('Missing fileId or userId');
    }

    // Fetch file from the database
    const file = await dbClient.db()
        .collection('files')
        .findOne({ _id: fileId, userId: userId });
    console.log('File fetched from database:', file);

    if (!file) {
        throw new Error('File not found');
    }

    const imagePath = file.localPath;
    const imageName = path.basename(imagePath);
    const outputDir = path.dirname(imagePath);

    // Check if the file exists
    if (!fs.existsSync(imagePath)) {
        throw new Error(`File does not exist at path: ${imagePath}`);
    }

    const widths = [500, 250, 100];

    for (const width of widths) {
        try {
            console.log(`Creating thumbnail for width ${width}...`);
            const options = { width: width };
            const thumb = await thumbnail(imagePath, options);

            const thumbnailPath = path.join(outputDir, `${imageName}_${width}`);
            fs.writeFileSync(thumbnailPath, thumb);
            console.log(`Thumbnail created: ${thumbnailPath}`);
        } catch (error) {
            console.error(`Error creating thumbnail for width ${width}:`, error.message);
            throw new Error(`Thumbnail creation failed for width ${width}`);
        }
    }

    console.log('Job completed successfully');
    return Promise.resolve();
});

